def ceil_sqrt(x):
	s, t = isqrt_rem(x)
	return s + (1 if t else 0)

def check_factors(p, q, N):
	return p * q == N

def factor_with_average(A, N):
	x = isqrt(A ** 2 - N)
	return (A - x, A + x)

def check_ch3(i, A, N):
	p, q = (div(A + i - 1, 3), div(A - i, 2))
	if check_factors(p, q, N):
		return p, q

	p, q = (div(A - i, 3), div(A + i - 1, 2))
	if check_factors(p, q, N):
		return p, q

	return None

def ch3_factor(N):
	# Valid when |3p - 2q| < N^(1/4)
	A = ceil_sqrt(6 * N)
	a = mpz(1)
	b = mpz(-1)
	c = -(A ** 2 - A - 6 * N)
	det = b ** 2 - 4 * a * c
