def factor(N):
	N = mpz(N)

	# Valid when |p-q| < 2N^(1/4)
	A = ceil_sqrt(N)
	p, q = factor_with_average(A, N)
	if check_factors(p, q, N):
		return (p, q)

	# Valid when |p-q| < 2^11 * N^(1/4)
	for i in range(2 ** 20):
		A += 1
		p, q = factor_with_average(A, N)
		if check_factors(p, q, N):
			return (p, q)

	return ch3_factor(N)

def decrypt_RSA(ciphertext, pk):
	N, e = pk
	p, q = factor(N)
	phiN = N - p - q + 1
	d = invert(e, phiN)
	return powmod(ciphertext, d, N)

